PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE dividend_events (
	id INTEGER NOT NULL, 
	symbol VARCHAR, 
	ex_date DATE, 
	pay_date DATE, 
	record_date DATE, 
	amount FLOAT NOT NULL, 
	source VARCHAR, 
	created_at DATETIME NOT NULL, 
	PRIMARY KEY (id), 
	CONSTRAINT uq_div_symbol_ex_amount UNIQUE (symbol, ex_date, amount)
);
CREATE TABLE user (
	id VARCHAR NOT NULL, 
	email VARCHAR, 
	created_at DATETIME NOT NULL, 
	PRIMARY KEY (id)
);
CREATE TABLE price (
	id INTEGER NOT NULL, 
	symbol VARCHAR NOT NULL, 
	date DATE NOT NULL, 
	close FLOAT NOT NULL, 
	PRIMARY KEY (id)
);
CREATE TABLE portfolio (
	id INTEGER NOT NULL, 
	user_id VARCHAR NOT NULL, 
	name VARCHAR NOT NULL, 
	created_at DATETIME NOT NULL, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES user (id)
);
CREATE TABLE holding (
	id INTEGER NOT NULL, 
	portfolio_id INTEGER NOT NULL, 
	symbol VARCHAR NOT NULL, 
	shares FLOAT NOT NULL, 
	avg_price FLOAT NOT NULL, 
	reinvest_dividends BOOLEAN NOT NULL, 
	PRIMARY KEY (id), 
	FOREIGN KEY(portfolio_id) REFERENCES portfolio (id)
);
CREATE TABLE user_profiles (
	id INTEGER NOT NULL, 
	user_id VARCHAR NOT NULL, 
	cash_balance FLOAT NOT NULL, 
	total_dividends_received FLOAT NOT NULL, 
	last_updated DATETIME NOT NULL, 
	PRIMARY KEY (id), 
	UNIQUE (user_id), 
	FOREIGN KEY(user_id) REFERENCES user (id)
);
CREATE TABLE dividend_payments (
	id INTEGER NOT NULL, 
	user_id VARCHAR NOT NULL, 
	portfolio_id INTEGER NOT NULL, 
	symbol VARCHAR NOT NULL, 
	ex_date DATE NOT NULL, 
	pay_date DATE, 
	amount_per_share FLOAT NOT NULL, 
	shares_owned FLOAT NOT NULL, 
	total_amount FLOAT NOT NULL, 
	reinvested BOOLEAN NOT NULL, 
	processed_at DATETIME NOT NULL, 
	PRIMARY KEY (id), 
	CONSTRAINT uq_div_payment_user_symbol_ex UNIQUE (user_id, symbol, ex_date), 
	FOREIGN KEY(user_id) REFERENCES user (id), 
	FOREIGN KEY(portfolio_id) REFERENCES portfolio (id)
);
CREATE INDEX ix_dividend_events_symbol ON dividend_events (symbol);
CREATE INDEX ix_dividend_events_ex_date ON dividend_events (ex_date);
COMMIT;
